class IDOCS_Sticky_TOC{constructor(){this.tocItems=document.querySelectorAll(".idocs-toc-item"),this.events()}events(){window.addEventListener("scroll",()=>{let t=window.scrollY;this.highlightTocItem(t)})}highlightTocItem(t){let e=-1;this.tocItems.forEach((c,s)=>{let i=document.getElementById(c.getAttribute("data-section-id"));i&&i.offsetTop-100<=t&&(e=s)}),this.tocItems.forEach((t,c)=>{c===e?t.classList.add("idocs-toc-item-active"):t.classList.remove("idocs-toc-item-active")})}}const instance_Sticky_TOC=new IDOCS_Sticky_TOC;